mapper.select:
SELECT board_id, board_name, board_desc, is_active, created_at, board_order,
allow_comment, allow_image, allow_file, write_role, banned_words, require_approval
FROM board_category WHERE board_id = #{board_id}

mapper.update:
UPDATE board_category
SET is_active = 0
WHERE board_id = #{board_id}

UPDATE board_category
SET is_active = 1
WHERE board_id = #{board_id}

UPDATE board_category
SET board_order = #{board_order}
WHERE board_id = #{board_id}

UPDATE board_category
SET allow_comment = #{value}
WHERE board_id = #{board_id}

UPDATE board_category
SET allow_image = #{value}
WHERE board_id = #{board_id}

UPDATE board_category
SET allow_file = #{value}
WHERE board_id = #{board_id}

UPDATE board_category
SET require_approval = #{value}
WHERE board_id = #{board_id}
