mapper.select:
SELECT COUNT(*) 
FROM lecture
WHERE user_num = #{user_num}

		SELECT category_num, category_main_name, category_detail
 	FROM category
 	WHERE category_num = #{category_num}

mapper.update:
	UPDATE category
	SET category_main_name = #{category_main_name},
    	category_detail = #{category_detail}
	WHERE category_num = #{category_num}
