mapper.select:
SELECT COUNT(*)
FROM community_content
WHERE category_main_num = #{category_main_num}

SELECT is_like
FROM community_content_like
WHERE post_id = #{post_id}
  AND user_num = #{user_num}

mapper.update:
UPDATE community_content_like
SET is_like = #{is_like}
WHERE post_id = #{post_id}
  AND user_num = #{user_num}

mapper.delete:
DELETE FROM community_content_like
WHERE post_id = #{post_id}
  AND user_num = #{user_num}
