mapper.select:
select * from lecture 
where lecture_author = #{lecture_author} 
and lecture_num != #{lecture_num}
order by readcount desc
limit 4

SELECT * FROM lecture WHERE lecture_num != #{lecture_num}
AND (
lecture_tag LIKE CONCAT('%', #{tag}, '%')
)
ORDER BY readcount DESC LIMIT 4

select * from chapter_detail
where chapter_num = #{chapter_num}
order by detail_order

select * from chapter
where lecture_num = #{lecture_num}
order by chapter_order

SELECT user_num
FROM user
WHERE user_id = #{user_id}

SELECT COUNT(*)
FROM enrollment
WHERE user_num = #{user_num}
AND lecture_num = #{lecture_num}
