mapper.select:
SELECT l.lecture_num, l.user_num, l.lecture_title, l.lecture_author, l.lecture_price, l.category_detail, l.readcount, l.lecture_img, l.lecture_detail, l.lecture_tag, COALESCE(ROUND(AVG(r.review_score), 1), 0) as avg_score, COALESCE(COUNT(DISTINCT r.review_num), 0) as review_count, COALESCE(COUNT(DISTINCT e.enrollment_id), 0) as student_count FROM lecture l LEFT JOIN review r ON l.lecture_num = r.lecture_num LEFT JOIN enrollment e ON l.lecture_num = e.lecture_num WHERE l.lecture_num != #{lecture_num}
AND (
l.lecture_tag LIKE CONCAT('%', #{tag}, '%')
)
GROUP BY l.lecture_num, l.user_num, l.lecture_title, l.lecture_author, l.lecture_price, l.category_detail, l.readcount, l.lecture_img, l.lecture_detail, l.lecture_tag ORDER BY l.readcount DESC LIMIT 4

select * from chapter_detail
where chapter_num = #{chapter_num}
order by detail_order

select * from chapter
where lecture_num = #{lecture_num}
order by chapter_order

SELECT user_num
FROM user
WHERE user_id = #{user_id}

SELECT COUNT(*)
FROM enrollment
WHERE user_num = #{user_num}
AND lecture_num = #{lecture_num}

SELECT COUNT(*)
FROM review
WHERE user_id = #{user_id}
AND lecture_num = #{lecture_num}
