mapper.insert:
INSERT INTO pointHistory (
    user_num, payment_id, point_change, type, description, created_at
) VALUES (
    #{user_num}, #{payment_id}, #{point_change}, #{type}, #{description}, NOW()
)

mapper.select:
SELECT COALESCE(SUM(point_change), 0)
FROM pointHistory
WHERE user_num = #{user_num}

mapper.update:
UPDATE user
SET points = points - #{point_change}
WHERE user_num = #{user_num}

UPDATE user
SET points = points + #{point_change}
WHERE user_num = #{user_num}
